import React from 'react';
import './App.css';

class Box extends React.Component {
  constructor(props){
    super(props);

    this.state = {
      boxColor: "red"
    };

    this.whenClicked = this.whenClicked.bind(this);
  }

  render() {
    return (
      <div id="box" className="box" onMouseOver={this.whenClicked} style={{backgroundColor: this.state.boxColor}}></div>
    );
  }

  getRandomNumber() {
      return Math.floor(Math.random() * 255);
  }

  whenClicked() {
    // don't ever do the line below in react
    // document.getElementById("box").style.backgroundColor = "magenta";

    // rgb(10, 10, 100)
    this.setState({boxColor: `rgb(${this.getRandomNumber()}, ${this.getRandomNumber()}, ${this.getRandomNumber()})`});
  }
}
export default Box;

