import React from 'react';
import './App.css';
import Box from './Box';

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      boxColors: ["red", "green", "blue"]
    };

    this.changeColor = this.changeColor.bind(this);
  }

  changeColor(color_index)
  {
    // get a copy of all my colors
    // change the one that needs to be changed
    // put all the colors back into the state

    let newColors = [...this.state.boxColors];
    newColors[color_index] = this.randomColor()
    this.setState({boxColors: newColors});
    console.log("Hi there!");
  }

  randomColor()
  {
    return "#" + Math.floor(Math.random() * 16777215).toString(16); // 00 00 00 - FF FF FF
  }

  render() {
    return (
      <div className="box-holder">
        <Box hue={this.state.boxColors[0]} onMouseOver={()=>this.changeColor(0)}/>
        <Box hue={this.state.boxColors[1]} onMouseOver={()=>this.changeColor(1)}/>
        <Box hue={this.state.boxColors[2]} onMouseOver={()=>this.changeColor(2)}/>
      </div>
    );
  }
}
export default App;

