import React from 'react';
import './App.css';
import Box from './Box';

class App extends React.Component {
  constructor(props) {
    super(props);

    const colors = []
    for(let i = 0; i < 100; i++)
    {
      colors.push("grey");
    }

    this.state = {
      boxColors: colors
    };

    this.changeColor = this.changeColor.bind(this);
  }

  changeColor(color_index)
  {
    // get a copy of all my colors
    // change the one that needs to be changed
    // put all the colors back into the state

    let newColors = [...this.state.boxColors];
    newColors[color_index] = this.randomColor()
    this.setState({boxColors: newColors});
    console.log("Hi there!");
  }

  randomColor()
  {
    return "#" + Math.floor(Math.random() * 16777215).toString(16); // 00 00 00 - FF FF FF
  }

  render() {

    const divs = [];
    for(let i = 0; i < this.state.boxColors.length; i++)
    {
      divs.push(<Box hue={this.state.boxColors[i]} onMouseOver={()=>this.changeColor(i)}/>);
    }

    const mapDivs = this.state.boxColors.map((color, index) => {
      return <Box hue={color} onMouseOver={()=>this.changeColor(index)} />
    });

    return (
      <div className="box-holder">
        {this.state.boxColors.map((color, index) => {
          return <Box hue={color} onMouseOver={()=>this.changeColor(index)} />
        })}
      </div>
    );
  }
}
export default App;

