import logo from './logo.svg';
import './App.css';
import React from 'react';
import Stat from './Stat.js';

class App extends React.Component {
  constructor(props) {
    super(props)


    this.state = {
      characterName: "Enter character name",
      strength: "Enter your strength",
      strengthBonus: "",
      dexterity: "Enter your dexterity",
      dexterityBonus: ""
    }
  }

  getModifier = (event) => {
    this.setState({strengthBonus : Math.floor((event.target.value - 10)/2)}); //gives NaN if it's a string
  }

  updateText = (event) => {
    this.setState({characterName: event.target.value});
  }

  render () {
    return (
      <div>
        <Stat baseStat={this.state.strength} statModifier={this.state.strengthBonus} modifier = {this.getModifier}/>
        {/* <Stat baseStat={this.state.dexterity} statModifier={this.state.dexterityBonus} modifier ={ ()=> this.getModifier(event, this.state.dexterity)}/> */}
        <input type="text" placeholder={this.state.characterName} onChange={this.updateText}/>
        <h2>{this.state.characterName}</h2>
      </div>
    );
  }
}

// App will hold all the input divs AND hold states
// Inputs will dynamically change based on states

export default App;

