using System;

namespace LinkdListLearning //or how I learned to stop worrying and love recursion
{
    class Program
    {
        static void Main(string[] args)
        {
            LinkedList list1 = new LinkedList();
            list1.head = new Node(1);
            Console.WriteLine(list1.head.value);
            Console.WriteLine("");

            list1.addFirst(0);
            Console.WriteLine(list1.head.value);
            Console.WriteLine(list1.head.nextNode.value);

            list1.addLast(2); // our addLast function requires to params, the value of the new node that you want to pass in, and at least one node to begin the process. there's always a "head", so we just used that node. 
            list1.printList();
            list1.addLast(3);
            list1.addLast(4);
            list1.deleteLast();
            list1.printList();

            list1.deleteFirst();
            list1.printList();

        }

     class Node //creating an object (called "Node")
    {
        public int value; //the value it holds
        public Node nextNode; //the next thing down the list

        public Node(int value) //this is a "constructor" that allows us to create a new node by passing in the value at the initial location of itself
        {
            this.value = value; //this is the value we passed in 
            this.nextNode = null; //this sets the location of the next node as null.. to be overrided later
        }
        public Node(int value, Node passedNode) //this constructor can "override" the previous one
        {
            this.value = value; // this is just like the previous constructor
            this.nextNode = passedNode; //this allows us to instert a node "before" the node we passed in
        }
        public void deleteNode()
            {

            }
        public void addLast(int data)
            {
                if (this.nextNode == null)
                {
                    this.nextNode = new Node(data);
                }
                else
                {
                    this.nextNode.addLast(data);
                }
            }
    }
    class LinkedList
    {
        public Node head;


            public void addLast(int value) //takes in the value
            {
                head.addLast(value); //passes the node head to the recursive version in the Node class

            }
        public void printList()
            {
                printList1(this.head); //passes the node head and calls the the recursive version
            }
        public void printList1(Node head)
            {
                if (head.nextNode != null) //if the next value is NOT null....
                {
                    Console.WriteLine(head.value); //print the current value
                    head = head.nextNode; //change the focus/parameter of the function
                    printList1(head); //call the function recursively with the newly changed parameter
                }
                else
                {
                    Console.WriteLine(head.value); //this is now the last node, so just print its value
                }
            }


            public void addFirst(int data) //takes in the new value you want and requires the current head
            {
                Node newfirst = new Node(data, head); //setsup a new Node with the value as the data and the nextNode as the previous head
                this.head = newfirst; //changes the location of the head to begin at that new node

            }
        public void deleteLast()
            {
                deleteLast1(head); //passes the specific Node.head to the recursive function
            }
        public void deleteLast1(Node head)
            {
                if (head.nextNode.nextNode != null) //looks two Nodes deep to check  for the null (seeking the last value)
                {
                    head = head.nextNode; //swaps the focus of the parameter to the next Node
                    deleteLast1(head); //recursive with the new parameter/focus
                }

                else
                {
                   head.nextNode = null; //stops the current focus from pointing at the next node. it floats away into the great digital sea of memory like Wilson the Volleyball
                }
            }

            public void deleteFirst()
            {
                this.head = this.head.nextNode;
            }
        }

    }
}

